window.onload=function(){document.querySelectorAll(".thumb-service").forEach(e=>{e.addEventListener("click",e=>{e.preventDefault();e=e.currentTarget.dataset.id;e=document.querySelector("#"+e),document.body.classList.add("overflow-hidden"),e.classList.add("open")})});document.querySelectorAll(".modal-bg, .close-modal").forEach(e=>{e.addEventListener("click",e=>{e.stopPropagation(),e.preventDefault();var o=e.currentTarget;switch(o.tagName){case"BUTTON":o.parentElement.parentElement.classList.remove("open");break;case"DIV":o.classList.remove("open")}document.body.classList.remove("overflow-hidden")})}),document.onkeydown=function(e){27==e.keyCode&&(document.querySelector(".modal-bg.open").classList.remove("open"),document.body.classList.remove("overflow-hidden"))}};