window.onload=function(){document.querySelectorAll(".thumb-service").forEach(e=>{e.addEventListener("click",e=>{e.preventDefault();e=e.currentTarget.dataset.id;document.querySelector("#"+e).classList.add("open")})});document.querySelectorAll(".modal-bg, .close-modal").forEach(e=>{e.addEventListener("click",e=>{e.stopPropagation(),e.preventDefault();var t=e.currentTarget;switch(t.tagName){case"BUTTON":t.parentElement.parentElement.classList.remove("open");break;case"DIV":t.classList.remove("open")}})})};